import React, { Component } from "react";
import SearchForm from "./SearchForm";
import ResultList from "./ResultList";
import API from "../utils/API"

class SearchResultContainer extends Component {

  state = {
    search: "",
    results: []
  };

  // When this component mounts, search the Giphy API for pictures of kittens
  componentDidMount() {
    this.searchBooks("kittens");
  }

  searchBooks = query => {
    API.search(query)
    .then(res => this.setState({results: res.data.items}),
  console.log(this.state.results))
      // .catch(err => console.log(err));
  };



  saveButton = buttonID => {
    let clickedButton = this.state.results[buttonID].volumeInfo;

    var NewMessageInput = React.createClass({
      postMessage: function(value) {
        axios.post('/saveBook', {
          title: value.title,
          author: value.author[0]
        })
        .then(function (message) {
          console.log(message);
        })

    let newTitle = clickedButton.title;
    let newAuthor = clickedButton.authors;
    console.log(newTitle, newAuthor[0]);
  }
})
  }

  handleInputChange = event => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
      [name]: value
    });
  };

  // When the form is submitted, search the Giphy API for `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.searchBooks(this.state.search);
  };

  render() {
    return (
      <div>
        <h1 className="text-center"> Search for a book:</h1>
        <SearchForm
          search={this.state.search}
          handleFormSubmit={this.handleFormSubmit}
          handleInputChange={this.handleInputChange}
        />
        <ResultList 
        results={this.state.results}
        saveButton={this.saveButton}
         />
      </div>
    );
  }
}

export default SearchResultContainer;
