import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import axios from "axios";

export default class LoginForm extends Component {
  constructor() {
    super();
    this.state = {
      username: "",
      password: ""
    };
  }

  handlePasswordChanged(event) {
    this.setState({ password: event.target.value });
  }

  handleUsernameChanged(event) {
    this.setState({ username: event.target.value });
  }

  handleSubmitForm(event) {
    event.preventDefault();
    //axios will come here to check log in and use ternary operator to go to the next page.
    alert("We need to check for log in and redirect to the route");
    // <Redirect to="/home">Login</Redirect>
  }
  render() {
    return (
      <div>

<form>
  <div className="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" name="username" className="form-control" id="username" aria-describedby="emailHelp" placeholder="Enter email"
    onChange={this.handleUsernameChanged.bind(this)}/>
    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div className="form-group">
    <label for="password">Password</label>
    <input type="password" name="password" className="form-control" id="password" placeholder="Password"
    onChange={this.handlePasswordChanged.bind(this)}/>
  </div>

  <button type="submit" className="btn btn-primary">Submit</button>
</form>




